import { createContext, useReducer } from "react"

const INITIAL_STATE = {
    oras:undefined,
    dates:[],
    optiuni:{
        adult:undefined,
        copii:undefined,
        camera:undefined,
    }
}

export const SearchCon = createContext(INITIAL_STATE)

const SearchReducer = (state,action) => {
    switch(action.type){
        case "Cautare_Noua" : 
            return action.payload
        case "Reset_Cautare" :
            return INITIAL_STATE
        default:
            return state
    }
}

export const SearchConProvider = ({copii}) => {
    const [state,dispatch] = useReducer(SearchReducer, INITIAL_STATE)
    
    return(
        <SearchCon.Provider value={{oras:state.oras, dates:state.dates, optiuni:state.optiuni, dispatch}}>
            {copii}
        </SearchCon.Provider>
    )

}
