import Navbar from '../NavigationBar/NavigationBar'
import SearchItem from '../SearchItem/SearchItem'
import useFetch from '../../../hooks/useFetch'
import List from '../../List/List'
import { useNavigate } from 'react-router-dom'
import { SearchContext } from '../../../context/SearchContext'
import { useContext } from 'react'
import React, { useState, useEffect } from 'react';

const AllRezervari = () => {
  const { data, loading, error, reFetch } = useFetch(`/hotelrooms`)
  const { dates } = useContext(SearchContext)

  const [selectedRoom, setSelectedRoom] = useState([])
  const handleClick = () => {
    reFetch()
  }
  const handleSelect = (e) => {
    const checked = e.target.checked
    const value = e.target.value
    setSelectedRoom(checked ? [...selectedRoom, value] : selectedRoom.filter(item => item !== value))
}

const isAvailable = (NumarCamera) => {
  const isFound = NumarCamera.unavailableDate.some(date =>
      alldates.includes(new Date(date).getTime())
  )
  return !isFound
}

  return (
    <><div>
      <Navbar />
      {data.map(item => (
                    <div className="reserveItem">
                        <div className="reserveItemInfo">
                            <div className="reserveTitle">{item.titlu}</div>
                            <div className="reserveDesc">{item.descriere}</div>
                            <div className="reserveMax">Numar maxim persoane: <b>{item.maxPersoane}</b></div>
                            <div className="reservePrice">{item.pret}</div>
                        </div>
                        <div className="reserveSelectRoom">
                            {item.numarcamere.map(NumarCamera => (
                                <div className="camera">
                                    <label>{NumarCamera.numar}</label>
                                    <input type="checkbox" value={NumarCamera._id} onChange={handleSelect} disabled={!isAvailable(NumarCamera)} />
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
      </div>
      </>
  );
}


export default AllRezervari;
