import Navbar from '../NavigationBar/NavigationBar';
import useFetch from '../../../hooks/useFetch';
import React, { useContext, useState } from 'react';
import "./AllRezervari.css"
import { AuthContext } from '../../../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const AllRezervari = () => {
  const [filterValue] = useState('price=212')
  const { data, loading, error, reFetch } = useFetch(`/hotelrooms?${filterValue}`)
  const handleClick = () => {
    reFetch()
  }

  const navigate = useNavigate()
const [openModal, setOpenModal] = useState(false);
const { user } = useContext(AuthContext)

const handleClickButton = () => {
    if (user) {
      setOpenModal(true)
    } else {
      navigate("/Login")
    }
  }
  return (
<div>
  <Navbar />
  {data.map(item => (
    <div class="reserveItem">
      <div class="reserveItemInfo">
        <div class="reserveTitle">{item.titlu}</div>
        <div class="reserveDesc">{item.descriere}</div>
        <div class="reserveMax">NumÄƒr maxim persoane: <b>{item.maxPersoane}</b></div>
        <div class="reservePrice">{item.pret}</div>
        <div></div>
      </div>
      <div class="reserveSelectRoom">
        {item.numarcamere.map(NumarCamera => (
          <div class="camera">
            <label>{NumarCamera.numar}</label>
            <div class="unavailableDate">
              {NumarCamera.unavailableDate.map(rezervat => (
                <div>data: {rezervat}</div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  ))}
</div>
  )
}


export default AllRezervari;
