import { createContext, useReducer } from "react"

const INITIAL_STATE = {
    oras:undefined,
    dates:[],
    Options:{
        adult:undefined,
        copil:undefined,
        camera:undefined,
    }
}

export const SearchContext = createContext(INITIAL_STATE)

const SearchReducer = (state,action) => {
    switch(action.type){
        case "NEW_SEARCH" : 
            return action.payload
        case "RESET_SEARCH" :
            return INITIAL_STATE
        default:
            return state
    }
}

export const SearchContextProvider = ({copil}) => {
    console.log("Trigger")
    const [state,dispatch] = useReducer(SearchReducer, INITIAL_STATE)

    return(
        <SearchContext.Provider value={{oras:state.oras, dates:state.dates, Options:state.Options, dispatch}}>
            {copil}
        </SearchContext.Provider>
    )

}
