import { useState } from 'react';
import "./AtractiiTuristice.css"
import NavigationBar from '../NavigationBar/NavigationBar';

const attractionsData = [
  {
    name: 'Bran Castle',
    location: 'Bran, Brașov County',
    description: 'Medieval fortress associated with the legend of Dracula.',
    image: 'https://via.placeholder.com/300x200',
  },
  {
    name: 'Peles Castle',
    location: 'Sinaia, Prahova County',
    description: 'Neo-Renaissance castle built for King Carol I.',
    image: 'https://via.placeholder.com/300x200',
  },
  {
    name: 'Corvin Castle',
    location: 'Hunedoara, Hunedoara County',
    description: 'Gothic-Renaissance castle also known as Hunyadi Castle.',
    image: 'https://via.placeholder.com/300x200',
  },
  // add more attractions as needed
];

const AttractionsPage = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const filteredAttractions = attractionsData.filter((attraction) =>
    attraction.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
        <NavigationBar />
    <div className="attractions-page">
      <h1>Attractions in Romania</h1>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search attractions"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      <div className="attractions-list">
        {filteredAttractions.map((attraction) => (
          <div key={attraction.name} className="attraction-card">
            <img src={attraction.image} alt={attraction.name} />
            <h2>{attraction.name}</h2>
            <p>{attraction.location}</p>
            <p>{attraction.description}</p>
          </div>
        ))}
      </div>
    </div>
    </div>
  );
};

export default AttractionsPage;
