import axios from "axios";
import { useContext, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { RegisterContext } from "../../context/RegisterContext";
import "./Register.css";
import background from "./Images/auth.jpg";

const Register = () => {
  const [credentials, setCredentials] = useState({
    username: undefined,
    password: undefined,
  });

  const {user, loading, error, dispatch } = useContext(RegisterContext)

  const navigate = useNavigate()

  const handleChange = (e) => {
    setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }))
  };

  const handleClick = async (e) => {
    e.preventDefault()
    dispatch({ type: "REGISTER_START" })
    try {
      const res = await axios.post("/auth/Register", credentials)
      dispatch({ type: "REGISTER_SUCCESS", payload: res.data })
      navigate("/")
    } catch (err) {
      dispatch({ type: "REGISTER_FAILURE", payload: err.response.data })
    }
  };

  console.log(user)

  return (
    <div style={{ backgroundImage: `url(${background})` }}>
    <><div className="NavBar">
          <div className="NavContainer">
              <Link to="/" style={{ color: "inherit", textDecoration: "none" }}>
                  <span className="Logo"><img className="Img" src="/Assets/Images/Logo.png" />Book And Go</span>
              </Link>
          </div>
      </div><div className="register">
              <div className="rContainer">
                  <input
                      type="text"
                      placeholder="username"
                      id="username"
                      onChange={handleChange}
                      className="rInput" />
                  <input
                      type="email"
                      placeholder="email"
                      id="email"
                      onChange={handleChange}
                      className="rInput" />
                  <input
                      type="password"
                      placeholder="password"
                      id="password"
                      onChange={handleChange}
                      className="rInput" />
                  <button disabled={loading} onClick={handleClick} className="rButton">
                      Register
                  </button>
                  {error && <span>{error.message}</span>}
              </div>
          </div></>
          </div>
  );
};

export default Register;
