import './Hotel.css'
import NavigationBar from "../Components/NavigationBar/NavigationBar"
import Header from "../Components/Header/Header"
import MailList from "../Components/EmailList/MailList"
import Footer from "../Components/Footer/Footer"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { useState } from 'react'
import { faCircleLeft, faCircleRight, faXmarkCircle } from '@fortawesome/free-regular-svg-icons'
import { faLocationDot } from '@fortawesome/free-solid-svg-icons'
import {useFetch} from "../src/hook/useFetch"
import { useLocation } from 'react-router-dom'

const Hotel = () => {
  const location = useLocation()
  console.log(location)
  const [SliderNumber, setSliderNumber] = useState(0);
  const [Open, setOpen] = useState(false);

  const {data,loading,error} = useFetch(`/hotels/`)

  const Photos = [
    {
      src: "/Assets/ImagesHotels/HD1.jpg"
    },
    {
      src: "/Assets/ImagesHotels/HD2.jpg"
    },
    {
      src: "/Assets/ImagesHotels/HD3.jpg"
    },
    {
      src: "/Assets/ImagesHotels/HD4.jpg"
    },
    {
      src: "/Assets/ImagesHotels/HD5.jpg"
    },
    {
      src: "/Assets/ImagesHotels/HD7.jpg"
    },
  ];
  
  const handleOpen = (i) =>{
    setSliderNumber(i);
    setOpen(true);
  }

  const handleMove =  (direction) => {
    let newSlideNumber;

    if (direction==="s"){
      newSlideNumber = SliderNumber === 0 ? 5 : SliderNumber-1
    } else {
      newSlideNumber = SliderNumber === 5 ? 0 : SliderNumber+1
    }

    setSliderNumber(newSlideNumber)
  }

  return (
    <div>
      <NavigationBar/>
      <Header type='List' />
      <div className="HotelContainer">
        {Open && <div className="Slider">
          <FontAwesomeIcon icon={faXmarkCircle} className="Close" onClick={()=>setOpen(false)}/>
          <FontAwesomeIcon icon={faCircleLeft} className="Arrow" onClick={()=>handleMove("s")}/>
          <div className="SliderWrapp">
            <img src={Photos[SliderNumber].src} alt="" className="SliderImage" />
          </div>
          <FontAwesomeIcon icon={faCircleRight} className="Arrow" onClick={()=>handleMove("d")}/>
          </div>}
        <div className="HotelWrapp">
          <button className="BookNow">Rezerva acum</button>
            <h1 className="HotelTitle">Hotel Dambovita</h1>
            <div className="HotelAddress">
              <FontAwesomeIcon icon={faLocationDot}/>
              <span>B-ul Libertatii, Nr.1, 130009 Târgovişte, România</span>
            </div>
            <span className="HotelDistance">Distanta buna - 1,1 km de centru</span>
            <span className="HotelPrice">Rezerva un sejur de peste 1200 RON la aceasta proprietate si obtine un discount la urmatorul sejur.</span>
          <div className="HotelImages">
            {Photos.map((photo,i) => (
              <div className='HotelImageWrapp'>
                <img onClick={()=>handleOpen(i)} src={photo.src} alt="" className="HotelImage" />
                </div>
                ))}
          </div>
          <div className="HotelDetail">
            <div className="HotelDetailText">
              <h1 className='HotelTitle'>Hotel Dambovita</h1>
              <p className="HotelDesc">
              Amplasat în centrul oraşului Târgovişte, la 200 de metri de Muzeul de Artă, Hotelul Dâmboviţa oferă unități de cazare cu aer condiționat și acces gratuit la internet WiFi, precum şi un restaurant şi bar. Oaspeții beneficiază de acces gratuit la centrul de fitness.
Unitățile de cazare sunt mobilate practic, având TV prin cablu şi baie privată cu duş sau cadă şi articole de toaletă gratuite. Unele camere sunt prevăzute cu balcon.
Alte facilităţi ale Hotelului Dâmboviţa includ parcare privată gratuită, precum şi un loc de joacă pentru copii. Serviciile de spălătorie sunt disponibile la un cost suplimentar.
Mănăstirea Dealu este la 5 km de hotel, iar Rezervaţia de Zimbri se află la 15 km. Gara Târgovişte se găsește la 1 km, iar la o scurtă plimbare de proprietate există o stație de autobuz.
              </p>
            </div>
            <div className="HotelDetailPrice">
              <h1>Perfect pentru o noapte!</h1>
              <span>
                Localizat aproape de centrul orasului, acest hotel are un scor al locatiei de 4.9!
              </span>
              <h2>
                <b>199 RON</b> (o noapte)
              </h2>
              <button>Rezerva acum!</button>
            </div>
          </div>
        </div>
        <MailList/>
        <Footer/>
      </div>
    </div>
  )
}


export default Hotel
