import axios from "axios";
import { useContext, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { RegisterContext } from "../../context/RegisterContext";
import "./Register.css";

const Register = () => {
  const [credentials, setCredentials] = useState({
    username: undefined,
    password: undefined,
  })
  const [email, setEmail] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const { user, loading, error, dispatch } = useContext(RegisterContext)

  const navigate = useNavigate()

  const navigateToLogin = () => {
    navigate('/Login')
  }

  const handleChange = (e) => {
    setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }))
  }

  const handleClick = async (e) => {
    e.preventDefault()
    dispatch({ type: "REGISTER_START" })
    try {
      const res = await axios.post("/auth/Register", credentials)
      dispatch({ type: "REGISTER_SUCCESS", payload: res.data })
      navigate("/")
    } catch (err) {
      dispatch({ type: "REGISTER_FAILURE", payload: err.response.data })
    }
  }

    const mailvalidation = (event) => {
      setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }))
      const { value } = event.target;
      setEmail(value);
      validateEmail(value);
    };

    const validateEmail = (value) => {
      const emailPattern = /^[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}$/;
  
      if (!emailPattern.test(value)) {
        setErrorMessage('Adresa de email nu este validă.');
      } else {
        setErrorMessage('');
      }
    };

  console.log(user)

  return (
    <div className="backgroundRegister" >
      <div className="register">
        <h2 className="registertext">Bine ai venit pe <Link to="/" style={{ color: "inherit", textDecoration: "none" }}>
          <span className="logotext">Book&Go</span>
        </Link></h2>
        <div className="rContainer">
          <input
            type="text"
            placeholder="Username"
            id="username"
            onChange={handleChange}
            className="rInput" />
          <input 
            type="email"
            placeholder="Email"
            id="email"
            value={email}
            onChange={mailvalidation}
            className="rInput"/>
          <input
            type="password"
            placeholder="Password"
            id="password"
            onChange={handleChange}
            className="rInput" />
          <input
            type="telefon"
            placeholder="Telefon"
            id="telefon"
            onChange={handleChange}
            className="rInput" />
          <input
            type="tara"
            placeholder="Tara"
            id="tara"
            onChange={handleChange}
            className="rInput" />
          <input
            type="oras"
            placeholder="Oras"
            id="oras"
            onChange={handleChange}
            className="rInput" />
          <button disabled={loading} onClick={handleClick} className="rButton">
            Register
          </button>
          {error && <span>{error.message}</span>}
          <button className="linkButtonRegister" onClick={navigateToLogin}>Ai deja cont? Logheaza-te aici.</button>
        </div>
        <div className="FooterText">Toate drepturile rezervate. Copyright © 2023 Book and Go.</div>
      </div>
    </div>
  )
}

export default Register;
