import { useContext } from "react";
import "./NavigationBar.css"
import {Link, useNavigate} from "react-router-dom"
import { AuthContext } from "../../../context/AuthContext";

const NavigationBar = () => {

  const { user } = useContext(AuthContext)

  const navigate = useNavigate()

  const navigateToLogin = () => {
    navigate('/Login')
  }

  const navigateToRegister = () => {
    navigate('/Register')
  }

  return (
    <div className="NavBar">
        <div className="NavContainer">
          <Link to="/" style={{color:"inherit", textDecoration:"none"}}>
          <span className="Logo"><img className="Img" src="/Assets/Images/Logo.png"/>Book And Go</span>
          </Link>
            {user ? user.username : (<div className="NavItems">
                <button onClick={navigateToRegister} className="NavButton">Register</button> 
                <button onClick={navigateToLogin} className="NavButton">Login</button>
            </div>)}
            <button
        id="LOGOUT"
        onClick={this.handleClick}
        disabled={this.state.disabled}
      >
        Log out
      </button>
        </div>
    </div>
  )
}

export default NavigationBar
