import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faXmarkCircle } from '@fortawesome/free-regular-svg-icons'
import "./Reserve.css"
import useFetch from "../../../hook/useFetch"
import { useState } from "react"

const Reserve = ({setOpen, hotelId}) => {

    const [selectedRoom, setSelectedRoom] = useState([])

    const { data, loading, error} = useFetch(`/hotels/camere/${hotelId}`)

    const handleSelect = (e) =>{
        const checked = e.target.checked
        const value = e.target.value
        setSelectedRoom(checked ? [...selectedRoom, value] : selectedRoom.filter(item=>item !==value))
    } 
    console.log(selectedRoom)
    return (
        <div className="reserve">
            <div className="reserveContainer">
                <FontAwesomeIcon icon={faXmarkCircle} className="reserveClose" onClick={()=>setOpen(false)}/>
                <span>Selecteaza camerele dorite:</span>
                {data.map(item=>(
                    <div className="reserveItem">
                        <div className="reserveItemInfo">
                            <div className="reserveTitle">{item.titlu}</div>
                            <div className="reserveDesc">{item.descriere}</div>
                            <div className="reserveMax">Numar maxim persoane: <b>{item.maxPersoane}</b></div>
                            <div className="reservePrice">{item.pret}</div>
                        </div>
                            {item.numarcamere.map(NumarCamera=>(
                            <div className="room">
                                <label>{NumarCamera.numar}</label>
                                <input type="checkbox" value={NumarCamera._id} onChange={handleSelect}/>
                            </div>    
                            ))}
                        </div>
                ))}
            </div>
        </div>
    )
}

export default Reserve
