import Navbar from '../NavigationBar/NavigationBar'
import SearchItem from '../SearchItem/SearchItem'
import useFetch from '../../../hooks/useFetch'
import List from '../../List/List'
import { useNavigate } from 'react-router-dom'
import { SearchContext } from '../../../context/SearchContext'
import { useContext } from 'react'
import React, { useState, useEffect } from 'react';

const AllRezervari = () => {
  const [data1, setData1] = useState(null);
  const [filterValue] = useState('price=212');
  const { data, loading, error, reFetch } = useFetch(`/hotelrooms?${filterValue}`)
  
  const handleClick = () => {
    reFetch()
  }

  useEffect(() => {
    fetch(`/hotels?featured=true&limit=4`)
      .then(response => response.json())
      .then(data1 => setData1(data1));
  });

  return (
    <><div>
      <Navbar />
      {data.map(item => (
                    <div className="reserveItem">
                        <div className="reserveItemInfo">
                            <div className="reserveTitle">{item.titlu}</div>
                            <div className="reserveDesc">{item.descriere}</div>
                            <div className="reserveMax">Numar maxim persoane: <b>{item.maxPersoane}</b></div>
                            <div className="reservePrice">{item.pret}</div><div></div>
                        </div>
                        <div className="reserveSelectRoom">
                            {item.numarcamere.map(NumarCamera => (
                                <div className="camera">
                                    <label>{NumarCamera.numar}</label>
                                    <div className="camera">
                                      {NumarCamera.unavailableDate.map(rezervat=> (
                                        <div>data:{rezervat}{hotel.map(item => {
                                            <p>{item.nume}</p>
                                        })}</div>
                                      ))}
                                    </div>
                                </div>
                                
                            ))}
                        </div>
                    </div>
                ))}
      </div>
      </>
  );
}


export default AllRezervari;
