import Navbar from '../NavigationBar/NavigationBar';
import useFetch from '../../../hooks/useFetch';
import React, { useState } from 'react';
import "./AllRezervari.css";

const AllRezervari = () => {
  const [filterValue] = useState('price=212');
  const { data, loading, error, reFetch } = useFetch(`/hotelrooms?${filterValue}`);
  const handleClick = () => {
    reFetch();
  };

  const filteredData = data.filter(item => {
    return item.numarcamere.some(NumarCamera => {
      return NumarCamera.unavailableDate.length > 0; 
    });
  });

  return (
    <div>
      <Navbar />
      {filteredData.map(item => (
        <div class="allrezervariItem">
          <div class="allrezervariItemInfo">
            <div class="allrezervariTitle">{item.titlu}</div>
            <div class="allrezervariDesc">{item.descriere}</div>
            <div class="allrezervariMax">NumÄƒr maxim persoane: <b>{item.maxPersoane}</b></div>
            <div></div>
          </div>
          <div class="allrezervariSelectRoom">
            {item.numarcamere.map(NumarCamera => (
              <div class="allrezervaricamera">
                <label>{NumarCamera.numar}</label>
                <div class="unavailableDate">
                  {NumarCamera.unavailableDate.map(rezervat => (
                    <div>data: {rezervat}</div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default AllRezervari;
